import Foundation

public struct BookInfo: Hashable {
    var bookId: Int
    var imageName: String
    var title: String
    var author: String
    var genre: String
    var country: String
    var description: String
    var published: String
    var currentStatus: CurrentBookNumberManager.BookStatus
}

public struct BookSeries: Hashable {
    var title: String
    var bookInfo: [BookInfo]
}

let genreList: [String:String] = ["horror":"공포", "thriller":"스릴러", "action":"액션", "wuxia":"무협", "sfFantasy":"SF/판타지"]

class HomeBookCollection {
    
    struct BookInfo: Hashable {
        var bookId: Int
        var imageName: String
        var title: String
        var author: String
        var genre: String
        var country: String
        var description: String
        var published: String
        let identifier: UUID = UUID()
        func hash(into hasher: inout Hasher) {
            hasher.combine(identifier)
        }
    }

    struct BookSeries: Hashable {
        var title: String
        var books: [BookInfo]
        let identifier: UUID = UUID()
        func hash(into hasher: inout Hasher) {
            hasher.combine(identifier)
        }
    }
    
    var collections: [BookSeries] {
        return _collections
    }
    
    fileprivate var _collections = [BookSeries]()
    
    init() {
        generateHomeBooksCollection()
    }
}

extension HomeBookCollection {
    func generateHomeBooksCollection() {
        _collections = [
            BookSeries(title: "😬 진격의 거인", books: [BookInfo(bookId: 20001, imageName: "진격의 거인-01", title: "진격의 거인 1화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "거인이 모든 것을 지배하는 세계.\n거인의 먹이가 되어 버린 인류는 거대한 벽을 쌓고\n벽 바깥으로 나가는 자유와 맞바꿔 침략을 막고 있었다.\n그러나 허울뿐인 평화는 벽을 넘어 버리는\n대거인의 등장으로 무너지고,\n절망적인 싸움이 시작되고 만다.", published: "2014년 8월 7일"),
BookInfo(bookId: 20002, imageName: "진격의 거인-02", title: "진격의 거인 2화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "거인이 지배하는 세계.\n아직 보지 못한 벽 바깥의 세계를 동경하는 엘런은\n훈련병이 되어 동료들과 함께 거인에 대항할 기술을 갈고닦는다.\n그러나 대거인의 등장으로 무수히 \n은 거인이 벽 안으로 침입.\n절친한 친구 아르민을 구하려던 엘런은\n그를 대신해 거인에게 잡아먹힌다…!!", published: "2014년 8월 7일"),
BookInfo(bookId: 20003, imageName: "진격의 거인-03", title: "진격의 거인 3화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "대형거인의 등장으로 벽이 붕괴되고, 무수히 많은 거인이 벽 안쪽으로 침입한다.\n미카사는 계속해서 거인을 쓰러뜨리지만, 엘런의 죽음을 알고 동요돼 위기에 빠지고 만다.\n그때 거인을 공격하는 의문의 거인이 나타나 미카사를 궁지에서 구해낸다.\n몇 구의 거인을 무찌르고 마침내 힘이 다한 의문의 거인.\n그 정체는?!!", published: "2014년 8월 7일"),
BookInfo(bookId: 20004, imageName: "진격의 거인-04", title: "진격의 거인 4화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "월 로제의 외벽에 뚫린 구멍을 막기 위해 ‘거인이 된 엘런이 거대한 바위로 구멍을 막는다’는 작전이 시작된다.\n인류의 존망을 걸고 거인으로 변한 엘런.\n그러나 자아를 잃고 폭주해 버린다.\n엘런은 아르민의 필사적인 질문에도 반응하지 않는데….", published: "2014년 8월 7일"),
BookInfo(bookId: 20005, imageName: "진격의 거인-05", title: "진격의 거인 5화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "거인이 된 엘런의 활약으로 인류는 월 로제의 트로스트 구 탈환에 성공한다.\n성공과 동시에 의식을 잃은 엘런이 눈을 뜨자 그곳에는 조사병단 단장 엘빈 스미스의 모습이.\n엘빈은 거인의 수수께끼가 잠들어 있는 엘런의 생가를 조사하기로 결심한다.\n그러기 위해서는 엘런의 힘이 필요하다고 하는데….", published: "2014년 8월 7일"),
BookInfo(bookId: 20006, imageName: "진격의 거인-06", title: "진격의 거인 6화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "엘런의 생가에 잠들어 있는\n‘거인의 수수께끼’를 알아내기 위해\n벽 바깥 조사를 나온 조사병단.\n거기에는 아르민과 미카사네의 모습도 보인다.", published: "2014년 8월 8일"),
BookInfo(bookId: 20007, imageName: "진격의 거인-07", title: "진격의 거인 7화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "엘런의 생가에 잠자고 있는 ‘거인의 수수께끼’를 찾아 벽 외 조사를 나선 조사병단.\n그 앞에 지성을 가진 ‘여성형 거인’이 나타났다.\n병단을 전멸 위기에 빠뜨리면서도 ‘여성형 거인’을 잡은 엘빈은 여성형 거인의 정체를 밝혀내려고 하는데….", published: "2014년 8월 8일"),
BookInfo(bookId: 20008, imageName: "진격의 거인-08", title: "진격의 거인 8화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "리바이 반의 동료를 잃은 엘런은\n‘여성형 거인’과 싸우지만 패배.\n황급히 달려온 미카사와 리바이의 도움으로 구출되지만,\n‘여성형 거인’을 포획하는 것은 실패하고 만다.", published: "2014년 8월 7일"),
BookInfo(bookId: 20009, imageName: "진격의 거인-09", title: "진격의 거인 9화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "마침내 ‘여성형 거인’을 잡는 것에\n성공한 엘런과 조사병단.\n그러가 때를 같이 하여.\n벽 안에서 잠자는 ‘새로운 수수께끼’에 직면한다.", published: "2014년 8월 7일"),
BookInfo(bookId: 20010, imageName: "진격의 거인-10", title: "진격의 거인 10화", author: "작가 ✍️ 이사야마 하지메 글 · 그림", genre: "장르 🎬 \(genreList["horror"] ?? "Error"), \(genreList["sfFantasy"] ?? "Error"), \(genreList["action"] ?? "Error")", country: "🇯🇵", description: "월 로제 부근에 갑자기 나타난 거인의 무리.\n104기 멤버들을 포함한 조사병단은 거인의 발생 지점을 알아내고자 서둘던 중.\n로제 부근의 우트가르드 성에서 밤을 보내게 된다.\n그러나 밤에는 활동할 수 없는 거인이 성을 에워싸고...!", published: "2014년 8월 7일"),
