import Foundation

public struct BookInfo: Hashable {
    var bookId: Int
    var imageName: String
    var title: String
    var author: String
    var genre: String
    var country: String
    var description: String
    var published: String
    var currentStatus: BookStatus
}

public struct BookSeries: Hashable {
    var title: String
    var bookInfo: [BookInfo]
}

let genreList: [String:String] = ["horror":"공포", "thriller":"스릴러", "action":"액션", "wuxia":"무협", "sfFantasy":"SF/판타지", "romance":"로맨스", "comedy":"코미디", "natural":"자연재해"]

class HomeBookCollection {
    struct BookInfo: Hashable {
        var bookId: Int
        var imageName: String
        var title: String
        var author: String
        var genre: String
        var country: String
        var description: String
        var published: String
        var currentStatus: BookStatus
        let identifier: UUID = UUID()
        func hash(into hasher: inout Hasher) {
            hasher.combine(identifier)
        }
    }

    struct BookSeries: Hashable {
        var title: String
        var books: [BookInfo]
        let identifier: UUID = UUID()
        func hash(into hasher: inout Hasher) {
            hasher.combine(identifier)
        }
    }
    
    var collections: [BookSeries] {
        return _collections
    }
    
    fileprivate var _collections = [BookSeries]()
    
    init() {
        generateHomeBooksCollection()
    }
}

